import './App.css';
import { useContext, useState } from 'react';
import { TodoCounter, TodoItem, TodoList, TodoSearch, CreateTodoButton } from './components';
import { useLocalStorage } from './hooks/useLocalStorage';
import { TodosLoading } from './components/TodosLoading/TodosLoading';
import { TodosError } from './components/TodosError/TodosError';
import { TodosEmptyList } from './components/TodosEmptyList/TodosEmptyList';
import { TodoContext, TodoProvider } from './components/TodoContext/TodoContext';


// Hook para usar localStorage PARA TODO! No solo TODOS



function App() {

  const {
    loading,
    error,
    searchedTodos,
    completeTodo,
    deleteTodo
} = useContext(TodoContext);

  return (
    <TodoProvider>
      <div className="App">
      {/* <TodoContext.Consumer>
          {({todos,loading})=>( */}
            <TodoCounter /*todos={todos} loading={loading}*/ /> 
          {/* )}
      </TodoContext.Consumer> */}

      <TodoSearch 
            // searchValue={searchValue}
            // setSearchValue={setSearchValue}
          />
      {/* <TodoContext.Consumer>
        {({
            loading,
            error,
            searchedTodos,
            completeTodo,
            deleteTodo
        }) => ( */}
          <TodoList /*todos={todos} loading={loading} error={error}*/>

            {loading && <TodosLoading />}
            {error && <TodosError />}
            {!loading && searchedTodos.length === 0 && <TodosEmptyList />}

            {searchedTodos.map(todo => (
              <TodoItem 
                name={todo.name} 
                key={todo.id}
                completed={todo.completed}
                onComplete={() => completeTodo(todo.id)} // Completar Todo!
                onDelete={() => deleteTodo(todo.id)} // Borrar Todo!
              />
            ))}


        </TodoList>
        {/* )}
      </TodoContext.Consumer> */}

      <CreateTodoButton />
    </div>
    </TodoProvider>

  );
}

export default App;
