import logo from './platzi.webp';
import './App.css';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { TodoItem } from './TodoItem';
import { TodoList } from './TodoList';
import { CreateTodoButton } from './CreateTodoButton'
import { useState } from 'react';



function useLocalStorage(itemName, initialValue)
{
  const localStorageItem = localStorage.getItem(itemName)
  let parsedItem;

  if (localStorageItem)
  {
    parsedItem = JSON.parse(localStorageItem)
  }
  else
  {
    parsedItem = []
    localStorage.setItem(itemName,JSON.stringify(initialValue))
  }

  const [item, setItem] = useState();

  const savedItem = (newItem) => 
  {
    setItem(newItem);
    localStorage.setItem(itemName,JSON.stringify(newItem))
  }

  return [item, savedItem]

}



function App() {

  //  const defaultTodos = [
  //    {id:1 ,name: "Aprender React JS", completed: true},
  //    {id:2 ,name: "Dominar React JS", completed: false},
  //    {id:3 ,name: "Crear mi propio Framework", completed: false},
  //    {id:4 ,name: "Conquistar el mundo", completed: true}
  //  ]

  // const [todos, setTodos] = useState(parsedTodos)
  const [todos, saveTodos] = useLocalStorage('TODOS_V1',[])

  const [searchValue, setSearchValue] = useState('')
  const searchedTodos = todos.filter(todo => todo.name.toLowerCase().includes(searchValue.toLowerCase()))

  const completeTodo = (id) => {
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(todo => todo.id === id)
    newTodos[todoIndex].completed = !newTodos[todoIndex].completed;
    saveTodos(newTodos)
  }

  const deleteTodo = (id) => {
    const newTodos = todos.filter(todo => todo.id !== id)
    saveTodos(newTodos)
  }

  return (
    <div className="App">
      <TodoCounter todos={todos}/>
      <TodoSearch 
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />
      <TodoList todos={todos}>
        {searchedTodos.map(todo => (
          <TodoItem 
            name={todo.name} 
            key={todo.id}
            completed={todo.completed}
            onComplete={() => completeTodo(todo.id)} // Completar Todo!
            onDelete={() => deleteTodo(todo.id)} // Borrar Todo!
          />
        ))}


      </TodoList>
      <CreateTodoButton />
    </div>
  );
}

export default App;
