import React, { useContext } from 'react'
import "./CreateTodoButton.css"
import { TodoContext } from '../TodoContext/TodoContext'

export const CreateTodoButton = () => {

  const {openModal, setOpenModal} = useContext(TodoContext);

  const toggleModal = () => {


    if (openModal === false)
    {
      setOpenModal(!openModal);
      document.getElementById('root').classList.toggle('modalAbierto');
      setTimeout(() => {
        document.getElementById('modalcito').style = "transform: translateX(450px);color:red;"
      },1)
    }
    else
    {
      setOpenModal(!openModal);
      document.getElementById('root').classList.toggle('modalAbierto');
      setTimeout(() => {
        document.getElementById('modalcito').style = "transform: translateX(900px);color:red;transition: 0.5s all ease-in-out;"
      },1)
    }


  }

  return (
    <button onClick={() => toggleModal()}>+</button>
  )
}
