import './App.css';
import { useState } from 'react';
import { TodoCounter, TodoItem, TodoList, TodoSearch, CreateTodoButton } from './components';
import { useLocalStorage } from './hooks/useLocalStorage';
import { TodosLoading } from './components/TodosLoading/TodosLoading';
import { TodosError } from './components/TodosError/TodosError';
import { TodosEmptyList } from './components/TodosEmptyList/TodosEmptyList';
import { TodoContext } from './components/TodoContext/TodoContext';


// Hook para usar localStorage PARA TODO! No solo TODOS



function App() {

  // Al usar objetos en el return de useLocalStorage.js debo llamar a mis componentes con los mismos nombres
  // por eso el nombre principal es item y saveItem. Luego con la sintaxis para renombrar estados le pongo
  // nuevamente los valores todos y saveTodos.
  // const { item: todos, 
  //         savedItem: saveTodos,
  //         loading,
  //         error} = useLocalStorage('TODOS_V1',[])

  // const [searchValue, setSearchValue] = useState('')
  // const searchedTodos = todos.filter(todo => todo.name.toLowerCase().includes(searchValue.toLowerCase()))

  // const completeTodo = (id) => {
  //   const newTodos = [...todos];
  //   const todoIndex = newTodos.findIndex(todo => todo.id === id)
  //   newTodos[todoIndex].completed = !newTodos[todoIndex].completed;
  //   saveTodos(newTodos)
  // }

  // const deleteTodo = (id) => {
  //   const newTodos = todos.filter(todo => todo.id !== id)
  //   saveTodos(newTodos)
  // }

  // Moviendo todo

  return (
    <TodoContext.Provider>
      <div className="App">
      {/* <TodoCounter todos={todos} loading={loading}/> */}
      <TodoCounter />

      <TodoSearch 
        // searchValue={searchValue}
        // setSearchValue={setSearchValue}
      />
      <TodoContext.Consumer>
        {({
            loading,
            error,
            searchedTodos,
            completeTodo,
            deleteTodo
        }) => (
          <TodoList todos={todos} loading={loading} error={error}>

          {loading && <TodosLoading />}
          {error && <TodosError />}
          {!loading && searchedTodos.length === 0 && <TodosEmptyList />}

          {searchedTodos.map(todo => (
            <TodoItem 
              name={todo.name} 
              key={todo.id}
              completed={todo.completed}
              onComplete={() => completeTodo(todo.id)} // Completar Todo!
              onDelete={() => deleteTodo(todo.id)} // Borrar Todo!
            />
          ))}


        </TodoList>
        )}
      </TodoContext.Consumer>

      <CreateTodoButton />
    </div>
    </TodoContext.Provider>

  );
}

export default App;
